import React, { useEffect, useState } from 'react'

import Axios from 'axios';
import { useNavigate } from 'react-router-dom';

const EditProfile = () => {
  
  
  const navigate = useNavigate();
  
  const [formdata,setformdata] = useState({
       name : '',
       contact_info : '',
  })
   
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [errormsg, setErrormsg] = useState("");

  const {name, contact_info} =formdata;
   
  const onChangeInput = (e) =>{
     setformdata({ ...formdata, [e.target.name]: e.target.value})
  }
  
  const onSubmitHandler = async () => {
    let response;  
  
    try {
      response = await Axios.get("http://127.0.0.1:8000/api/recipient/alluser/", {});
      console.log( "the data is successfully loaded")
      console.log(response.data);
  
      // Update the state with user data
      setformdata(response.data.data)
  
      console.log(response.data.message);
      if (response.data.message === "Login successful") {
        setIsLoggedIn(true);
      } else {
        setErrormsg(response.data.message);
      }
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };
  
  useEffect(() => {
    // This effect will run when the 'name' state changes
    // and will display the updated name in the console
    console.log('Name has been updated:', name);
  }, [name]);


  return (
  <>
   <form onSubmit={onSubmitHandler}>
          <h3>Register Here</h3>
          {isLoggedIn ? (
            <p className="errormsg">123</p>
          ) : (
            <p className="errormsg">{errormsg}</p>
          )}
          <div className="form-feilds">
            <div className="left">
              <label htmlFor="username">Name</label>
              <input
                onChange={onChangeInput}
                value={name}
                type="text"
                placeholder="Enter Name"
                name="name"
                required
              />
             
            </div>
            <div className="right">
              
              <label htmlFor="contact_info">Contact Info</label>
              <input
                value={contact_info}
                onChange={onChangeInput}
                type="text"
                placeholder="Enter Contact Info"
                id="contact_info"
                name="contact_info"
                required
              />
             
            </div>
          </div>
          <button type="submit">Log In</button>
          <a href="/login">Already Registered? Sign-In</a>
        </form>
      <div className='lower'>

      <h1>{name}</h1>   
      </div>
  </>
  )
}

export default EditProfile
