import React from 'react'
import { useState } from 'react'
import Axios from 'axios'
import './EditProfile.css';

const EditProfile = () => {
  
  const [userData, setUserData] = useState(
    {
      name : '',
      contact_info:'',
    }
  )

  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [errormsg, setErrormsg] = useState("");
  
   
  const { username, password, name, email,contact_info,confirm_password } = formData;


  const onChangeInput = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };



  const onSubmitHandler = async (e) => {
    e.preventDefault();
    const fetchUserData = async () => {
      try {
        const response = await Axios.get("http://127.0.0.1:8000/api/recipient/alluser/", {
          headers: {
            Authorization: "token 3de2cd0cef5ce4c86730bd8cacb777ea6b62cc56", // Replace with your authentication token
          },
  
      });
        
        setUserData(
  
          {
            name : response.data.data.name,
            contact_info:response.data.data.contact_info,
          }
        );
        console.log(response.data.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };
    fetchUserData();
   
    console.log(response.data.message);
    if (response.data.message == "Login successful") {
      setIsLoggedIn(true);
    } else {
      setErrormsg(response.data.message);
    }
  };
  
  


  return (
    <>
    

    <form onSubmit={onSubmitHandler}>
          <h3>Register Here</h3>
          {isLoggedIn ? (
            <p className="errormsg">123</p>
          ) : (
            <p className="errormsg">{errormsg}</p>
          )}
          <div className="form-feilds">
            <div className="left">
              <label htmlFor="username">Name</label>
              <input
                onChange={onChangeInput}
                value={name}
                type="text"
                placeholder="Enter Name"
                name="name"
                required
              />
             
             
            </div>
            <div className="right">
              
              <label htmlFor="contact_info">Contact Info</label>
              <input
                value={contact_info}
                onChange={onChangeInput}
                type="text"
                placeholder="Enter Contact Info"
                id="contact_info"
                name="contact_info"
                required
              />
              
            </div>
          </div>
          <button type="submit">Log In</button>
          <a href="/login">Already Registered? Sign-In</a>
        </form>
    </>
  )
}

export default EditProfile
