import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import './EditProfile.css';

const EditProfile = () => {
  const [userData, setUserData] = useState({
    username:'',
    name: '',
    contact_info: '',
    email :'',
  });
  const onChangeInput = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };
  const handlePatchAPI = async () => {
    try {
      const response = await Axios.patch(
        "http://127.0.0.1:8000/api/recipient/update-user/",
        {
          name: userData.name,
          contact_info: userData.contact_info,
        },
        {
          headers: {
            Authorization: "token 7386ff07e42ea1ad485ae2b35e57a2c7c6311f5d", // Replace with your authentication token
          },
        }
      );

      console.log("Profile updated successfully:", response.data);
      // You can add a success message or redirect the user to another page
    } catch (error) {
      console.error('Error updating user profile:', error);
      // Handle the error, display an error message, or redirect the user accordingly
    }
  };

  const {  name, contact_info,username,email } = userData;

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await Axios.get("http://127.0.0.1:8000/api/recipient/alluser/", {
          headers: {
            Authorization: "token 7386ff07e42ea1ad485ae2b35e57a2c7c6311f5d", // Replace with your authentication token
          },
        });

        setUserData({
          name:response.data.data.name,
          contact_info:response.data.data.contact_info,
          username:response.data.data.username,
          email:response.data.data.email,
        });
        console.log(response.data.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []); // Empty dependency array ensures this effect runs once when the component mounts

  return (
    <>
      
      <form >
          <h3>Register Here</h3>
         
          <div className="form-feilds">
            <div className="left">
              <label htmlFor="username">Name</label>
              <input
                onChange={onChangeInput}
                value={name}
                type="text"
                placeholder="Enter Name"
                name="name"
                required
              />
              <label htmlFor="email">Email</label>
              <input
                value={email}
                onChange={onChangeInput}
                type="email"
                placeholder="Enter Email"
                id="email"
                name="email"
                required
                readOnly
              />
             
            </div>
            <div className="right">
              <label htmlFor="username">Username</label>
              <input
                value={username}
                onChange={onChangeInput}
                type="text"
                placeholder="Enter Username"
                id="username"
                name="username"
                required
                readOnly
              />
              <label htmlFor="contact_info">Contact Info</label>
              <input
                value={contact_info}
                onChange={onChangeInput}
                type="text"
                placeholder="Enter Contact Info"
                id="contact_info"
                name="contact_info"
                required
              />
              
            </div>
          </div>
          <button type="submit">Log In</button>
          <a href="/login">Already Registered? Sign-In</a>
        </form>
      
    </>
  );
};

export default EditProfile;
