import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import './EditProfile.css';

const EditProfile = () => {
  const [userData, setUserData] = useState({
    name: '',
    contact_info: '',
  });
  const onChangeInput = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const {  password, name, email,contact_info,confirm_password } = formData;

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await Axios.get("http://127.0.0.1:8000/api/recipient/alluser/", {
          headers: {
            Authorization: "token 7386ff07e42ea1ad485ae2b35e57a2c7c6311f5d", // Replace with your authentication token
          },
        });

        setUserData({
          name:response.data.data.name,
          contact_info:response.data.data.contact_info,
        });
        console.log(response.data.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []); // Empty dependency array ensures this effect runs once when the component mounts

  return (
    <>
      <div className='leftrectangle1'>
        hello {userData.name}
      </div>
      <form onSubmit={onSubmitHandler}>
          <h3>Register Here</h3>
          {isLoggedIn ? (
            <p className="errormsg">123</p>
          ) : (
            <p className="errormsg">{errormsg}</p>
          )}
          <div className="form-feilds">
            <div className="left">
              <label htmlFor="username">Name</label>
              <input
                onChange={onChangeInput}
                value={name}
                type="text"
                placeholder="Enter Name"
                name="name"
                required
              />
              <label htmlFor="email">Email</label>
              <input
                value={email}
                onChange={onChangeInput}
                type="email"
                placeholder="Enter Email"
                id="email"
                name="email"
                required
              />
              <label htmlFor="password">Password</label>
              <input
                value={password}
                onChange={onChangeInput}
                type="password"
                placeholder="Enter Password"
                id="password"
                name="password"
                required
              />
            </div>
            <div className="right">
              <label htmlFor="username">Username</label>
              <input
                value={username}
                onChange={onChangeInput}
                type="text"
                placeholder="Enter Username"
                id="username"
                name="username"
                required
              />
              <label htmlFor="contact_info">Contact Info</label>
              <input
                value={contact_info}
                onChange={onChangeInput}
                type="text"
                placeholder="Enter Contact Info"
                id="contact_info"
                name="contact_info"
                required
              />
              <label htmlFor="confirm_password">Confirm Password</label>
              <input
                value={confirm_password}
                onChange={onChangeInput}
                type="text"
                placeholder="Enter Confirm Password"
                id="confirm_password"
                name="confirm_password"
                required
              />
            </div>
          </div>
          <button type="submit">Log In</button>
          <a href="/login">Already Registered? Sign-In</a>
        </form>
      
    </>
  );
};

export default EditProfile;
